def dividing():
	fpok = open('../../MC1 Data/pok','r')
	ftime = open('../Pre-Processing/articleTime.csv','r')
	articleTime = []
	cnt = 0
	for line in ftime:
		if cnt!=0:
			articleTime.append(int(line.split(',')[1]))
		cnt += 1	

	for i in range(845):
		if not os.path.exists('./ByDay/'+str(articleTime[i])):
			os.mkdir('./ByDay/'+str(articleTime[i]))
		shutil.copy('../../MC1 Data/articles/'+str(i)+'.txt','./ByDay/'+str(articleTime[i])+'/'+str(i)+'.txt')

def main():
	dividing()

if __name__=='__main__':
	main()